import { ModeModel } from '../models/ModeModel.js';
import { GameObjectFactory } from '../helpers/game-object-factory.js';
const TutorialMode = new ModeModel({ name: 'Tutorial', campaignList: [GameObjectFactory.createCampaign('Tutorial Basics', 1, 5)] }),
  ChallengeMode = new ModeModel({ name: 'Challenge', campaignList: [GameObjectFactory.createCampaign('Novice Challenge', 5, 10), GameObjectFactory.createCampaign('Advanced Challenge', 5, 15)] }),
  EndlessMode = new ModeModel({ name: 'Endless', campaignList: [], stageGenerationMethod: () => GameObjectFactory.createStageModel({ randomize: !0 }) });
export const Modes = { TutorialMode: TutorialMode, ChallengeMode: ChallengeMode, EndlessMode: EndlessMode };
