import StageModel from '../models/StageModel.js';
import SetModel from '../models/SetModel.js';
import CampaignModel from '../models/CampaignModel.js';
const GameObjectFactory = {
  createStageModel: (e = {}) => new StageModel({ kx: e.kx || 0, kw: e.kw || 0, kh: e.kh || 0, tx: e.tx || 0, ty: e.ty || 0, tw: e.tw || 0, th: e.th || 0 }),
  createStageSet: (e = 'Unnamed Set', t = 10) => new SetModel({ name: e, stages: Array.from({ length: t }, () => GameObjectFactory.createStageModel()) }),
  createCampaign: (e = 'Unnamed Campaign', t = 5, a = 10) => new CampaignModel({ name: e, sets: Array.from({ length: t }, () => GameObjectFactory.createStageSet('Set', a)) }),
};
export default GameObjectFactory;
